let desktopResults=document.getElementById("desktopSection"),screenshotDIR="/linuxHQ/screenshots/";function displayDEOutput(n,e,t,s,o,a){const i=`\n\n        <div class="card w-40">\n            <div class="card__header py-2">\n                <a href="${t}" target="_blank" rel="noopener"> ${e}</a>\n            </div>\n\n            <div class="card__body d-none d-md-inline-block">\n                <figure>\n                    <a href="${screenshotDIR}${s}" target="_blank">\n                    <img class="thumbnail-fluid--lg" src="${screenshotDIR}${o}" alt="{currentDETitle} Screenshot" loading="lazy">\n\n                    <figcaption>\n                        <span class="font-weight-bold">Distro:</span>\n                        ${a}\n                    </figcaption>\n                    </a>\n                </figure>\n\n            </div>\n        </div>\n\n\n        `;desktopResults.insertAdjacentHTML("beforeend",i)}function getDesktopData(n){$(document).ready((function(){jsonURL="/linuxHQ/json/desktops.json",$.getJSON(jsonURL,(function(n){$.each(n,(function(n,e){for(let n=0;n<e.length;n++){displayDEOutput(e[n].desktopName,e[n].desktopTitle,e[n].generalInfo.desktopHomepage,e[n].screenshots.src,e[n].screenshots.thumbnail,e[n].screenshots.distro)}}))}))}))}