let desktopResults=document.getElementById("desktopSection"),screenshotDIR="/linuxHQ/screenshots/";function displayDEOutput(currentDEName,currentDETitle,currentDEHomepage,currentDEScreenshot,currentDEScreenshotTN,currentDESShotDistro){const deInfoCard=`\n\n        <div class="card w-40">\n            <div class="card__header py-2">\n                <a href="${currentDEHomepage}" target="_blank" rel="noopener"> ${currentDETitle}</a>\n            </div>\n\n            <div class="card__body d-none d-md-inline-block">\n                <figure>\n                    <a href="${screenshotDIR}${currentDEScreenshot}" target="_blank">\n                        <img class="thumbnails--lg img-fluid" src="${screenshotDIR}${currentDEScreenshotTN}" alt="{currentDETitle} Screenshot" loading="lazy">\n                    \n                    <figcaption>\n                        <span class="font-weight-bold">Distro:</span> \n                        ${currentDESShotDistro}\n                    </figcaption>\n                    </a>\n                </figure>\n\n            </div>\n        </div>\n\n\n        `;desktopResults.insertAdjacentHTML("beforeend",deInfoCard)}function getDesktopData(data){$(document).ready((function(){jsonURL="/linuxHQ/json/desktops.json",$.getJSON(jsonURL,(function(data){$.each(data,(function(key,value){let currentFramework=key;for(let subFrameworks=0;subFrameworks<value.length;subFrameworks++){let currentDEName,currentDETitle,currentDEHomepage,currentDESShot,currentDESShotTN,currentDESShotDistro;displayDEOutput(value[subFrameworks].desktopName,value[subFrameworks].desktopTitle,value[subFrameworks].generalInfo.desktopHomepage,value[subFrameworks].screenshots.src,value[subFrameworks].screenshots.thumbnail,value[subFrameworks].screenshots.distro)}}))}))}))}